mapper.delete:
delete from user_roles
where user_id = #{userId, typeHandler=com.homeputers.ebal2.api.mybatis.typehandler.UUIDTypeHandler}
  and role = #{role}

mapper.select:
select count(distinct user_id)
from user_roles
where role = #{role}

select count(distinct user_id)
from user_roles
where role = #{role}
  and user_id <> #{userId, typeHandler=com.homeputers.ebal2.api.mybatis.typehandler.UUIDTypeHandler}
